  const handleInput=(e)=>{
    setTodoList.append(e.target.value)



  }







  return (
    <div className="App">


        <div className='addtask'>
           <input onChange={handleInput}/>
           <p>{newTask}</p>
           <button>Add Task</button></div>
        <div className='list'></div>










    </div>
  );
}



export default App;
