import {  useEffect, useRef, useState } from 'react';
import './App.css';
import Task from './Task';
// import {v4} from 'uuid'


function App() {
  // const [completed,setCompleted]=useState(false)
  // const toggle=()=>{
  //   setCompleted(!completed)
  //   document.getElementsByid('task').removeClass('task').addClass('tasks')

  const [todoList,setTodoList]=useState([])
  const [newTask,setNewTask]=useState('')
  const handleChange=(e)=>{
    setNewTask(e.target.value)

  }
//   const todoNameRef=useRef()
//   useEffect(()=>{
//    const storedTodos=JSON.parse(localStorage.getItem('MY_APP_STATE'))
//    if (storedTodos) setTodoList(storedTodos)
//   },[])
//   useEffect(() => {
//   window.localStorage.setItem('MY_APP_STATE', JSON.stringify(todoList));
// }, [todoList]);


  const a=useRef()

  useEffect(()=>{
    window.localStorage.setItem('List',JSON.stringify(todoList))

  },[todoList])

  useEffect(()=>{
    const data=window.localStorage.getItem('List')
    if (data!==null)
     setTodoList(JSON.parse(data))




   },[])




  const addTask=()=>{
    const b=a.current.value

    const task={
      id:todoList.length===0 ?1 :todoList[todoList.length-1].id+1,
      taskName:newTask,
      completed:false
    }
    if (b==='') return
   setTodoList(prevTodos=>{
     return [...prevTodos,{id:v4(),name:name,complete:false}]
   })
   todoNameRef.current.value=null

  }

  const deleteTask=(id)=>{
  setTodoList(todoList.filter((task)=>task.id!==id))
}

  const completeTask=(id)=>{
    setTodoList(todoList.map((task)=>{
      if (task.id===id){
        return {...task,completed:true};
      }else{

        return task

      }
    }))
  }








  return (
    <div className="App">


        <div className='addTask'>
           <input onChange={handleChange}/>

           <button onClick={addTask}>Add Task</button>

        </div>
        <div className='list'>

          {todoList.map((task)=>{
            return <Task taskName={task.taskName} id={task.id} completed={task.completed} deleteTask={deleteTask} completeTask={completeTask} />


          })}



        </div>










    </div>
  );
}



export default App;
