import React from "react";


function Task(props) {
  return (
    <div className="task" style={{backgroundColor:props.completeTask(props.id)}}>
      <p>{props.taskName}</p>
      <button onClick={() => props.completeTask(props.id)}>Completed</button>
      <button onClick={() => props.deleteTask(props.id)}>x</button>
    </div>
  );
}

export default Task;
