import React from 'react'


class LikeButton extends React.component{
 constructor(props){
  super(props);
  this.state={liked:false}
 }
 toggle(){
  this.setState({liked:!this.state.liked})
 }
 render(){
  var name=this.props.name;
  var text=this.state.liked?'Unlike':'Like';
  var myColor=this.state.liked?'red':'black';
  var weight=this.state.liked?'bold':'normal'
  return (<>
   <p><span style={{color:myColor,fontWeight:weight}}>
    {name}</span></p>
    <span onclick={this.toggle.bind(this)}>Be</span>
    </>
  )


 }
}
export default LikeButton
